import React from 'react'
import classnames from 'classnames'

function InputGroup(props) {
    return (
        <div className="mb-3">
            <label for="Age" className="form-label">{props.label}</label>
            <input type={props.type} className={(classnames("form-control", {"is-invalid": errors}))} name={props.name} onChange={props.onChangeHandler}/>
            
            {
                props.errors && (<div  className='invalid-feedback'>
                {props.errors}
            </div>)
            }
        </div>
        
    )
}

export default InputGroup
