import React, { useEffect, useState } from 'react'
import InputGroup from '../components/InputGroup'
import RowDetails from '../components/RowDetails'
import axios from 'axios'

function Home() {
    const [users, setUsers] = useState([])
    const [form, setForm] = useState({})

    const onChangeHandler = (e)=>{
        setForm({
        ...form,
        [e.target.name]: e.target.value
        })
        console.log(form);
    }


    /* find all user */
    useEffect( ()=>{
         axios.get('/api/users')
        .then(res=>{
            setUsers(res.data)
        })
    }, [])
    return (
        <div className="row p-4">
            {/* <div className="alert alert-success d-flex align-items-center" role="alert">
                <svg className="bi flex-shrink-0 me-2" width="24" height="24" role="img" aria-label="Success:"><use xlink:href="#check-circle-fill" /></svg>
                <div>
                    An example success alert with an icon
                </div>
            </div> */}
            <div className="mt-4">
                <h2>Crud Customer</h2>
            </div>
            <div className="col-12 col-lg-4">
                <form>
                    <InputGroup label="Customer Name" type="text" name="customerName" onChangeHandler={onChangeHandler}/>
                    <InputGroup label="Customer Adress" type="text" name="customerAdress" onChangeHandler={onChangeHandler}/>
                    <InputGroup label="Customer Mobile Number" type="text" name="customerMobileNumber" onChangeHandler={onChangeHandler}/>
                    <button className="btn btn-primary">Add Customer</button>
                </form>
            </div>
            <div className="col-12 col-lg-7">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope='col'>Customer Name</th>
                            <th scope='col'>Customer Adress</th>
                            <th scope='col'>Customer Mobile Number</th>
                            <th scope='col'>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(({customerName, customerAdress, customerMobileNumber})=>(
                                <RowDetails customerName={customerName} customerAdress={customerAdress} customerMobileNumber={customerMobileNumber} />
                            ))
                        }
                    
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Home
